using MatontineDigitalApp.Commons;
using MatontineDigitalApp.Model;
using MatontineDigitalApp.Pages.Profile;
using Microsoft.Maui.Controls;
using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;

namespace MatontineDigitalApp.Pages.inscription
{
    public partial class SelectCountryView : ContentPage
    {
        private readonly Action<CountryDto> _onCountrySelected;
        private readonly string _defaultCountryCode;
        private EditProfilePage parentPage;

        //public SelectCountryView(Page parentPage, Action<CountryDto> onCountrySelected, string defaultCountryCode, Action<object> countrySelectedAction)
        //{
        //    InitializeComponent();
        //    _onCountrySelected = onCountrySelected;
        //    _defaultCountryCode = defaultCountryCode;
        //    RefreshData();
        //}

        public SelectCountryView(EditProfilePage parentPage, Action<CountryDto> onCountrySelected, string defaultCountryCode)
        {
            InitializeComponent();
            this.parentPage = parentPage;
            _onCountrySelected = onCountrySelected;
            _defaultCountryCode = defaultCountryCode;
            RefreshData();
        }

        private void RefreshData()
        {
            Title = "Select Country";
            GetListItems();
        }

        private void GetListItems()
        {
            if (CommonsResources.Countries == null || !CommonsResources.Countries.Any())
            {
                CommonsResources.Countries = LoadDefaultCountries();
            }

            //foreach (var country in CommonsResources.Countries)
            //{
            //    country.FlagImage = GetImageFromBase64(country.FlagBase64);
            //}

            CountryListView.ItemsSource = CommonsResources.Countries;
        }

        private async void CountryListView_ItemTapped(object sender, ItemTappedEventArgs e)
        {
            if (e.Item is CountryDto selectedCountry)
            {
                _onCountrySelected?.Invoke(selectedCountry);
                await Navigation.PopModalAsync();
            }
        }


        private List<CountryDto> LoadDefaultCountries()
        {
            return new List<CountryDto>
            {
                new CountryDto
                {
                    Code = "SN",
                    Description = "Senegal",
                    FlagBase64 = ""
                },
                new CountryDto
                {
                    Code = "GB",
                    Description = "Gambia",
                    FlagBase64 = ""
                },

            };
        }

    }
}
